mapper.insert:
INSERT
  INTO MEMBER_TEST(
       MEMBER_ID
     , MEMBER_NAME
     , DEPT_CODE
     , AGE
     ) VALUES(
       #{memberId}
     , #{memberName}
     , #{deptCode}
     , #{age}
     )

mapper.update:
UPDATE MEMBER_TEST
   SET MEMBER_NAME = #{memberName}
     , DEPT_CODE = #{deptCode}
     , AGE = #{age}
 WHERE MEMBER_ID = #{memberId}
